select.where:
     and username like concat('%', #{username}, '%')
     and password = #{password}
     and nick_name like concat('%', #{nickName}, '%')
     and phone = #{phone}
     and avatar = #{avatar}
     and sex = #{sex}
     and memo = #{memo}
     and open_id = #{openId}
     and union_id = #{unionId}
     and last_login_ip = #{lastLoginIp}
     and last_login_time = #{lastLoginTime}
     and status = #{status}
and del_flag = 0
